#include <iostream>
using namespace std;

char genome[] = {'N', 'O', 'P'};
bool done = false;
string res;

bool is_thue(string curr_word)
{
    int sz = curr_word.size();
    int i = 1, j = 2;
    while (i <= sz / 2)
    {
        if (curr_word.substr(sz - j, i) == curr_word.substr(sz - i, i))
            return false;
        i++, j += 2;
    }
    return true;
}

void thue_sequence(string curr_word, int n)
{
    if (!done)
    {
        string temp;
        if (curr_word.size() == n)
        {
            done = true;
            res = curr_word;
            return;
        }
        for (int i = 0; i < 3; i++)
        {
            temp = curr_word + genome[i];
            if (is_thue(temp))
                thue_sequence(temp, n);
        }
    }
}

